import axios from "axios"
import { GET_ERRORS, FETCH_USER_SUCCESS} from "./types";

export const getCurrentExpert = user => dispatch => {
    axios
      .post("http://localhost:5000/api/current", user)
      .then(res => {
        dispatch({
            type: FETCH_USER_SUCCESS,
            payload: res.data
        });
      })
      .catch(err =>
        dispatch({
          type: GET_ERRORS,
          payload: err.response.data
        })
      );
  };


export const updateCurrentExpert = currentUser => dispatch => {
    axios
      .post("http://localhost:5000/api/current/update", currentUser)
      .then(res => {
        dispatch({
            type: FETCH_USER_SUCCESS,
            payload: res.data
        });
      })
      .catch(err =>
        dispatch({
          type: GET_ERRORS,
          payload: err.response.data
        })
      );
  };
