import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser } from "actions/authActions";
import { Link } from "react-router-dom";
import NotificationAlert from "react-notification-alert";

import {
  Button,
  Card,
  CardImg,
  CardBody,
  Input,
  FormGroup,
  Row,
  Col
} from "reactstrap";
import bg from "assets/img/flower-back.png";

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      errors: ""
    };
  }
  componentDidMount() {
    // If logged in and user navigates to Login page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      this.props.history.push("/admin/dashboard");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isAuthenticated) {
      this.props.history.push("/admin/dashboard");
    }

    if (nextProps.errors) {
      console.log('----------', nextProps.errors)
      this.setState({
        errors: nextProps.errors
      });

    }
  }

  componentDidUpdate(prevProps) {
    if (this.state.errors !== prevProps.errors) {
      this.notify("tr");
    }

  }
  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  handleLogin = () => {
    const userData = {
      email: this.state.email,
      password: this.state.password
    };

    this.props.loginUser(userData);
  };
  notify = place => {
    console.log('-----his.state.errors-----', this.state.errors)
    var options = {};
    options = {
      place: place,
      message: (
        <div>
          <div>
           Error: {this.state.errors}
          </div>
        </div>
      ),
      type: 'warning',
      icon: "tim-icons icon-bell-55",
      autoDismiss: 7
    };
    this.refs.notificationAlert.notificationAlert(options);
  };
  render() {
    // const { errors } = this.state;
    console.log('-------errors------', this.state.errors)

    return (
      <>
        <Row>
        <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Col md='5' className='p-0'>
            <Card className='mb-0'>
              <CardImg width="100%" className='background-tint h-100' src={require("assets/img/theme/auth.png")}>
              </CardImg>
            </Card>
          </Col>
          <Col md='7' className='p-0 '>
            <Card className='h-100 mb-0'>
            <div className="content"
          style={{
            backgroundImage: `url(${bg})`,
            backgroundPosition: "right top",
            backgroundRepeat: "no-repeat",
          }}>
              <CardBody className='mt-4 p-4'>
                <Row>
                  <Col md="8">
                    <h1>
                      Login
                   </h1>
                  </Col>
                </Row>
                <Row>
                  <Col md="8">
                    <FormGroup>
                      <Input
                        onChange={this.onChange}
                        value={this.state.email}
                        id="email"
                        type="email"
                        placeholder="Enter your email address"
                      />
                    </FormGroup>
                    <FormGroup>
                      <Input
                        placeholder="Enter your password"
                        onChange={this.onChange}
                        value={this.state.password}
                        id="password"
                        type="password"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md='4'>
                    <Button className="btn-block"
                      color="success"
                      type="button"
                      size="lg"
                      onClick={() => {
                        this.handleLogin();
                      }}
                    >
                      Login
                  </Button>
                  </Col>
                </Row>
                  <Row className="top-margin-row-high">

                    <Col md="8">
                    <hr/>
                    Don't have account?&nbsp;&nbsp;&nbsp;<Link className="link-text" to="/auth/register">Sign Up</Link>
                    </Col>
                  </Row>
              </CardBody>
            </div>

            </Card>
          </Col>
        </Row>

      </>
    );
  }
}
Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.string.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});
export default connect(
  mapStateToProps,
  { loginUser }
)(Login);
