import React from "react";
import { connect } from "react-redux";
import ReactDatetime from "react-datetime";
import moment from "moment";
import { getCurrentExpert, getBankInfo, updateCurrentExpert, createOrUpdateBankInfo, getExpertScheduale, updateExpertScheduale, uploadImage, getExpertGallery } from "actions/userActions";
import SwiperCore, { Navigation, Pagination, Scrollbar, A11y, EffectCoverflow } from 'swiper';
// import { Swiper, SwiperSlide } from 'swiper/react';
import Gallery from 'react-grid-gallery';

import 'swiper/swiper.scss';
import {
  Button,
  Modal,
  ModalBody,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  FormGroup,
  Input,
  Row,
  Col,
  Container,
  Table
} from "reactstrap";
import NotificationAlert from "react-notification-alert";
import bg from "assets/img/flower-back.png";
import 'swiper/components/effect-coverflow/effect-coverflow.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
SwiperCore.use([EffectCoverflow]);
SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

class Settings extends React.Component {
  notify = place => {
    var options = {};
    options = {
      place: place,
      message: (
        <div>
          <div>
            Profile successfuly updated.
          </div>
        </div>
      ),
      type: 'success',
      icon: "tim-icons icon-bell-55",
      autoDismiss: 7
    };
    this.refs.notificationAlert.notificationAlert(options);
  };

  constructor(props) {
    super(props);
    this.fileInput = React.createRef()
    this.state = {
      userId: '',
      modalBank: false,
      modalPersonalDetails: false,
      modalPhotoGallery: false,
      modalAvailability: false,
      modalNotification: false,
      about: '',
      status: false,
      rowKey: null,
      isEdit: false,
      image: null,
      scheduale: {
        availability: []
      },
      slidesPerView: 'auto',
      gallery: [],
      ibanNumber: '',
      accountNumber: '',
      bankName: '',
      bankAddress: '',
      swiftCode: '',
      fullName: '',
      userProfile: {
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        lang: '',
        phoneNumber: '',
        status: '',
        role: '',
        gender: '',
        dateOfBirth: '',
        venueName: '',
        residential: {
          street: '',
          number: '',
          city: '',
          country: ''
        },
        venue: {
          street: '',
          number: '',
          city: '',
          country: ''
        }
      }
    }
    this.onSelectImage = this.onSelectImage.bind(this);
  }
  onEdit = (id) => {

    this.setState({
      status: true,
      rowKey: id
    })
  }

  onCancel = () => {
    const user = {
      role: 'expert'
    };
    this.props.getExpertScheduale(user);
    this.setState({
      status: false,
      rowKey: null
    })
  }
  onSave = () => {
    const user = {
      scheduale: this.state.scheduale
    };
    this.props.updateExpertScheduale(user);
    this.setState({
      status: false,
      rowKey: null
    })
  }
  componentDidMount() {
    const user = {
      role: 'expert'
    };
    this.setState({
      userId: localStorage.getItem('userId')
    })

    this.props.getBankInfo();
    this.props.getCurrentExpert(user);
    this.props.getExpertScheduale(user);
    this.props.getExpertGallery(user);
  }

  onSelectImage (index, image) {
    var images = this.state.images.slice();
    var img = images[index];
    if(img.hasOwnProperty("isSelected"))
        img.isSelected = !img.isSelected;
    else
        img.isSelected = true;

    this.setState({
        images: images
    });
}
  toggleModalBank = () => {
    this.setState({
      modalBank: !this.state.modalBank
    });
  };
  toggleModalGallery = () => {
    this.setState({
      modalPhotoGallery: !this.state.modalPhotoGallery
    });
  };
  togglePersonalDetails = () => {
    this.setState({
      modalPersonalDetails: !this.state.modalPersonalDetails
    });
  };
  toggleAvailability = () => {
    this.setState({
      modalAvailability: !this.state.modalAvailability
    });
  };
  handleProfileUpdate = () => {
    const current = {
      currentUser: this.state.userProfile
    };
    this.props.updateCurrentExpert(current);
    this.togglePersonalDetails();
    this.notify("br");
  }
  imageUpload = () => {
    let formData = new FormData();
    formData.append("photo", this.state.image);
    formData.append("user_id", '5f89b74c785a191b10dab1ac')
    this.props.uploadImage(formData);
  }
  triggerInputFile = () => {
    if (this.fileInput.current !== undefined && this.fileInput.current.click !== undefined)
      this.fileInput.current.click()
  }

  onChangeImage = (event) => {

    if (event.target.files && event.target.files[0]) {
      this.setState({ image: event.currentTarget.files[0] })
      let imageFormObj = new FormData();
      imageFormObj.append("photo", event.target.files[0]);
      this.props.uploadImage(imageFormObj);
    }
  }
  handleFromChange = (event) => {

    let items = [...this.state.scheduale.availability];
    let item = items.find(x => x._id === this.state.rowKey);
    item.from = event.target.value;
    this.setState({
      data: items.map(el => (el._id === item._id ? { ...el, item } : el))
    }
    );
  }
  handleToChange = (event) => {

    let items = [...this.state.scheduale.availability];
    let item = items.find(x => x._id === this.state.rowKey);
    item.to = event.target.value;
    this.setState({
      data: items.map(el => (el._id === item._id ? { ...el, item } : el))
    }
    );
  }
  handleStatusChange = (event) => {

    let items = [...this.state.scheduale.availability];
    let item = items.find(x => x._id === this.state.rowKey);
    item.status = event.target.value;
    this.setState({
      data: items.map(el => (el._id === item._id ? { ...el, item } : el))
    }
    );
  }
  onChange = e => {
    let obj = this.state.userProfile;
    obj[e.target.id] = e.target.value;
    this.setState({ userProfile: obj })
  };
  onChangeDateOfBirth = (e) => {
    let date = moment(e.toDate()).format('MM/DD/YYYY');
    let obj = this.state.userProfile;
    obj.dateOfBirth = date;
    this.setState({ userProfile: obj })
  }
  onChangeResidential = e => {
    let obj = this.state.userProfile;
    obj.residential[e.target.id] = e.target.value;
    this.setState({ userProfile: obj })
  };
  onChangeVenue = e => {
    let obj = this.state.userProfile;
    obj.venue[e.target.id] = e.target.value;
    this.setState({ userProfile: obj })
  };
  onChangeBankInfo = e => {
    this.setState({
      [e.target.id]: e.target.value
    })
  };
  componentWillReceiveProps(nextProps) {
    if (nextProps.user.bankInfo !== null && nextProps.user.bankInfo !== undefined) {
      this.setState({
        userId: nextProps.user.bankInfo.userId,
        ibanNumber: nextProps.user.bankInfo.ibanNumber,
        accountNumber: nextProps.user.bankInfo.accountNumber,
        bankName: nextProps.user.bankInfo.bankName,
        bankAddress: nextProps.user.bankInfo.bankAddress,
        swiftCode: nextProps.user.bankInfo.swiftCode,
        fullName: nextProps.user.bankInfo.fullName
      });
    }
    this.setState({
      userProfile: nextProps.user.payload,
      scheduale: nextProps.user.scheduale,
      gallery: nextProps.user.gallery

    })
  };

  updateBankDetails = () => {
    let data = {
      bankInfo: {
        ibanNumber: this.state.ibanNumber,
        accountNumber: this.state.accountNumber,
        bankName: this.state.bankName,
        bankAddress: this.state.bankAddress,
        swiftCode: this.state.swiftCode,
        fullName: this.state.fullName,
      }
    }
    this.props.createOrUpdateBankInfo(data);
    this.toggleModalBank();
  }

  render() {
    const loading = !this.props.user.loading;
    const IMAGES =
[{
        src: "https://c2.staticflickr.com/9/8817/28973449265_07e3aa5d2e_b.jpg",
        thumbnail: "https://c2.staticflickr.com/9/8817/28973449265_07e3aa5d2e_n.jpg",
        thumbnailWidth: 320,
        thumbnailHeight: 174,
        isSelected: true,
        caption: "After Rain (Jeshu John - designerspics.com)"
},
{
        src: "https://c2.staticflickr.com/9/8356/28897120681_3b2c0f43e0_b.jpg",
        thumbnail: "https://c2.staticflickr.com/9/8356/28897120681_3b2c0f43e0_n.jpg",
        thumbnailWidth: 320,
        thumbnailHeight: 212,
        tags: [{value: "Ocean", title: "Ocean"}, {value: "People", title: "People"}],
        caption: "Boats (Jeshu John - designerspics.com)"
},

{
        src: "https://c4.staticflickr.com/9/8887/28897124891_98c4fdd82b_b.jpg",
        thumbnail: "https://c4.staticflickr.com/9/8887/28897124891_98c4fdd82b_n.jpg",
        thumbnailWidth: 320,
        thumbnailHeight: 212
}]
    return (
      <>
        <div className="content"
          style={{
            backgroundImage: `url(${bg})`,
            backgroundPosition: "right top",
            backgroundRepeat: "no-repeat",
          }}
        >
          <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Container>
            <Row>
              <Col lg="3" />
              <Col lg="3">
                <Card className="card-settings" style={{
                  textAlign: "center",
                }} onClick={this.togglePersonalDetails}>
                  <CardHeader >
                    <CardTitle>
                      Personal Details
                                    </CardTitle>
                  </CardHeader>
                  <CardBody >
                    <img
                      alt="..."
                      src={require("assets/img/icon_salon_chair.png")}
                    />
                  </CardBody>
                </Card>
              </Col>
              <Col lg="3">
                <Card className="card-settings" style={{
                  textAlign: "center"
                }} onClick={this.toggleModalBank}>
                  <CardHeader>
                    <CardTitle >
                      Bank Details
                                    </CardTitle>
                  </CardHeader>
                  <CardBody >
                    <img
                      alt="..."
                      src={require("assets/img/icon_wallet.png")}
                    />
                  </CardBody>
                </Card>
              </Col>
            </Row>
            <Row>
              <Col lg="3" />
              <Col lg="3">
                <Card className="card-settings" style={{
                  textAlign: "center"
                }} onClick={this.toggleAvailability}>
                  <CardHeader >
                    <CardTitle>
                      Availability
                                    </CardTitle>
                  </CardHeader>
                  <CardBody >
                    <img
                      alt="..."
                      src={require("assets/img/icon_availability.png")}
                    />
                  </CardBody>
                </Card>
              </Col>
              <Col lg="3">
                <Card className="card-settings" style={{
                  textAlign: "center"
                }} onClick={this.toggleModalGallery}>
                  <CardHeader>
                    <CardTitle>
                      Photo Gallery
                                    </CardTitle>
                  </CardHeader>
                  <CardBody>
                    <img
                      alt="..."
                      src={require("assets/img/icon_photo.png")}
                    />
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </Container>
          <Modal
            modalClassName="modal-services"
            isOpen={this.state.modalBank}
            toggle={this.toggleModalBank}>
            <div className="modal-header justify-content-center">
              <button
                aria-label="Close"
                className="close"
                data-dismiss="modal"
                type="button"
                onClick={this.toggleModalBank}
              >
                <i className="tim-icons icon-simple-remove" />
              </button>
              <h4 className="title title-up">Bank Details</h4>
            </div>
            <ModalBody >
              <div className="content">
                <Row>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Expert ID</label>
                      <Input
                        disabled
                        value={this.state.userId}
                        type="string"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-md-1" md="6">
                    <FormGroup>
                      <label>Full Name</label>
                      <Input
                        onChange={this.onChangeBankInfo}
                        value={this.state.fullName}
                        type="text"
                        id="fullName"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>IBAN number</label>
                      <Input
                        onChange={this.onChangeBankInfo}
                        value={this.state.ibanNumber}
                        type="text"
                        id="ibanNumber"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-md-1" md="6">
                    <FormGroup>
                      <label>Account number</label>
                      <Input
                        onChange={this.onChangeBankInfo}
                        value={this.state.accountNumber}
                        type="text"
                        id="accountNumber"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Bank Name</label>
                      <Input
                        onChange={this.onChangeBankInfo}
                        value={this.state.bankName}
                        type="text"
                        id="bankName"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-md-1" md="6">
                    <FormGroup>

                      <label>Bank Address</label>
                      <Input
                        onChange={this.onChangeBankInfo}
                        value={this.state.bankAddress}
                        type="text"
                        id="bankAddress"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Swift Code</label>
                      <Input
                        onChange={this.onChangeBankInfo}
                        value={this.state.swiftCode}
                        type="text"
                        id="swiftCode"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col>
                    <Button className="btn-block"
                      color="success"
                      type="button"
                      onClick={this.updateBankDetails}
                    >
                      Save
                  </Button>
                  </Col>
                </Row>
              </div>
            </ModalBody>
          </Modal>
          <Modal
            modalClassName="modal-services"
            isOpen={this.state.modalPersonalDetails}
            toggle={this.togglePersonalDetails}>
            <div className="modal-header justify-content-center">
              <button
                aria-label="Close"
                className="close"
                data-dismiss="modal"
                type="button"
                onClick={this.togglePersonalDetails}
              >
                <i className="tim-icons icon-simple-remove" />
              </button>

            </div>
            <ModalBody >
              {(loading && this.state.userProfile) ? (
                <div className="content">
                  <Row>
                    <Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>First Name</label>
                        <Input
                          id="firstName"
                          value={this.state.userProfile.firstName || ''}
                          type="text"
                          onChange={this.onChange}
                        />
                      </FormGroup>
                    </Col>
                    <Col className="pl-md-1" md="6">
                      <FormGroup>
                        <label>Second Name</label>
                        <Input
                          id="lastName"
                          value={this.state.userProfile.lastName || ''}
                          onChange={this.onChange}
                          type="text"
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row style={{ marginTop: "30px" }}>
                    <Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>Phone</label>
                        <Input
                          id="phoneNumber"
                          value={this.state.userProfile.phoneNumber || ''}
                          onChange={this.onChange}
                          type="text"
                        />
                      </FormGroup>
                    </Col>
                    <Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>Email</label>
                        <Input
