import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
import { Link } from "react-router-dom";
// reactstrap components
import {
  Collapse,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  Input,
  NavbarBrand,
  Navbar,
  Button,
  NavLink,
  Nav,
  Container,
  Modal,
  NavItem
} from "reactstrap";


import logo from "assets/img/brand/stylchi-logo.png";
import translate from "assets/img/lang.png";
import map from "assets/img/location-icon.png";

class HomeNavbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collapseOpen: false,
      modalSearch: false,
      color: "navbar-transparent"
    };
  }
  componentDidMount() {
    window.addEventListener("resize", this.updateColor);
  }
  componentWillUnmount() {
    window.removeEventListener("resize", this.updateColor);
  }
  // function that adds color white/transparent to the navbar on resize (this is for the collapse)
  updateColor = () => {
    if (window.innerWidth < 993 && this.state.collapseOpen) {
      this.setState({
        color: "bg-white"
      });
    } else {
      this.setState({
        color: "navbar-transparent"
      });
    }
  };
  // this function opens and closes the collapse on small devices
  toggleCollapse = () => {
    if (this.state.collapseOpen) {
      this.setState({
        color: "navbar-transparent"
      });
    } else {
      this.setState({
        color: "bg-white"
      });
    }
    this.setState({
      collapseOpen: !this.state.collapseOpen
    });
  };
  // this function is to open the Search modal
  toggleModalSearch = () => {
    this.setState({
      modalSearch: !this.state.modalSearch
    });
  };
  render() {
    return (
      <>
        <Navbar
          className={classNames("navbar-absolute", this.state.color)}
          expand="lg"
        >
          <Container fluid>
            <div className="navbar-wrapper">
              <NavbarBrand href="#pablo" onClick={e => e.preventDefault()}>
              <img
                  alt="..."
                  src={require("assets/img/brand/stylchi-logo.png")}
                />
              </NavbarBrand>
            </div>
            <button
              aria-expanded={false}
              aria-label="Toggle navigation"
              className="navbar-toggler"
              data-target="#navigation"
              data-toggle="collapse"
              id="navigation"
              type="button"
              onClick={this.toggleCollapse}
            >
              <span className="navbar-toggler-bar navbar-kebab" />
              <span className="navbar-toggler-bar navbar-kebab" />
              <span className="navbar-toggler-bar navbar-kebab" />
            </button>
            <Collapse navbar className="justify-content-end" isOpen={this.state.collapseOpen}>
            <ul class="navbar-nav ml-auto">
                        <li class="nav-item active">
                            <a class="nav-link" href="!#">
                                For Partners{" "}
                                <span class="sr-only">(current)</span>
                            </a>
                        </li>
                        <li class="nav-item dropdown">
                            <a
                                class="nav-link dropdown-toggle"
                                href="!#"
                                id="navbarDropdown"
                                role="button"
                                data-toggle="dropdown"
                                aria-haspopup="true"
                                aria-expanded="false"
                            >
                                <span>
                                    <img src={map} alt="" className="mr-2" />
                                </span>
                                Estonia
                            </a>
                            <div
                                class="dropdown-menu"
                                aria-labelledby="navbarDropdown"
                            >
                                <a class="dropdown-item" href="!#">
                                    Action
                                </a>
                                <a class="dropdown-item" href="!#">
                                    Another action
                                </a>
                                <div class="dropdown-divider"></div>
                                <a class="dropdown-item" href="!#">
                                    Something else here
                                </a>
                            </div>
                        </li>
                        <li class="nav-item dropdown">
                            <a
                                class="nav-link dropdown-toggle"
                                href="!#"
                                id="navbarDropdown"
                                role="button"
                                data-toggle="dropdown"
                                aria-haspopup="true"
                                aria-expanded="false"
                            >
                                <span>
                                    <img
                                        src={translate}
                                        alt=""
                                        className="mr-2"
                                    />
                                </span>
                                EN
                            </a>
                            <div
                                class="dropdown-menu"
                                aria-labelledby="navbarDropdown"
                            >
                                <a class="dropdown-item" href="!#">
                                    EST
                                </a>
                                <div class="dropdown-divider"></div>
                                <a class="dropdown-item" href="!#">
                                    RUS
                                </a>
                            </div>
                        </li>
                        <li class="nav-item">
                        <Button style={{ marginBottom: "10px" }} color="success" >Sign up
                                  </Button>
                                  <Button style={{ marginBottom: "10px" }} color="success" >Log In
                                  </Button> 
                        </li>

                    </ul>
            </Collapse>
          </Container>
        </Navbar>
      </>
    );
  }
}

export default HomeNavbar;
