import React from "react";
import {
  Button,
  Modal,
  ModalBody,
  ListGroup, ListGroupItem,
  Input,
  FormGroup,
  Row,
  Col,
  Container

} from "reactstrap";
import { getCategories, createExpertService} from "actions/serviceActions";
import { connect } from "react-redux";
class Services extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modalCategory: false,
      dropdownOpen: false,
      setDropdownOpen: false,
      categories: [],
      service: {
        serviceName: '',
        price: '',
        duration: ''
      },
      category: {}
    };
  }
  toggle = (prevState) => {
    this.setState({
      setDropdownOpen: !prevState
  });
  }
  componentDidMount() {
    this.props.getCategories();
  }
  componentWillReceiveProps(nextProps) {
    this.setState({
      categories: nextProps.service.categories
        })
  };
  toggleModalSearch = () => {
    this.setState({
        modalCategory: !this.state.modalCategory
    });
  };
  createExpertService = () => {
    const serviceData= {
      service: this.state.service,
      category: this.state.category
    }
    console.log('----------------', serviceData)
    //this.props.createExpertService(serviceData);
  }
  onChangeCategory = (event) => {
    let items = [...this.state.categories];
    let item = items.find(x => x.categoryName === event.target.value);
    this.setState({
      category: item
    })
  }
  onChange = e => {
    let obj = this.state.service;
    obj[e.target.id] = e.target.value;
    this.setState({ service: obj })
  }
  render() {
    return (
      <>
        <div className="content">
        <Row>
            <Col md="8">
              <h1>Services</h1>
            </Col>
            <Col
              md="4">
                  <div style={{ float:'right'}}>
              <Button color="warning" size="md" onClick={this.toggleModalSearch}>
              Add Services
            </Button>
            </div>
            </Col>
          </Row>
          <Container>
        <Row>
           <Col>
           <h1>Hair Cut</h1>
           <ListGroup>
           <ListGroupItem className=" d-flex justify-content-between align-items-center">
               <span> Men's Hair Cut{" "}</span>
               <span> 1hr{" "}</span>
               <span> €25{" "}</span>
               <span>
                   <Button className="btn-icon" color="success" size="sm">
                    <i className="fa fa-edit"></i>
                </Button>{` `}
                <Button className="btn-icon" color="danger" size="sm">
                    <i className="fa fa-times" />
                </Button></span>
        </ListGroupItem>
        <ListGroupItem className=" d-flex justify-content-between align-items-center">
               <span> Kids's Hair Cut{" "}</span>
               <span> 1hr{" "}</span>
               <span> €25{" "}</span>
               <span>
                   <Button className="btn-icon" color="success" size="sm">
                    <i className="fa fa-edit"></i>
