import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "actions/authActions";
import NotificationAlert from "react-notification-alert";
import PhoneInput from 'react-phone-input-2';
import bg from "assets/img/flower-back.png";
import partner from "assets/img/theme/partner.png";
import PrivacyPolicy from 'assets/static/PrivacyPolicy.pdf';
import PartnerPolicy from 'assets/static/PartnerPolicy.pdf';
import 'react-phone-input-2/lib/bootstrap.css'
import {
  Button,
  Card,
  CardImg,
  CardBody,
  Input,
  FormGroup,
  Row,
  Col,
  Form
} from "reactstrap";
import { Link } from "react-router-dom";

class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      phoneNumber: "",
      email: "",
      role: "expert",
      password: "",
      password2: "",
      language: "ENG",
      allowContact: false,
      errors: ""
    };
  }
  componentDidMount() {
    // If logged in and user navigates to Login page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      this.props.history.push("/admin");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };
  handleCheckboxChange = event => {
    const target = event.target;   
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [event.target.id]: value
    });
  }
  handleRegister = (e) => {
    e.preventDefault();
    const data = {
      userData: {
        firstName: this.state.firstName,
      lastName: this.state.lastName,
      phoneNumber: this.state.phoneNumber,
      email: this.state.email,
      role: this.state.role,
      password: this.state.password,
      password2: this.state.password2,
      language: this.state.language,
      allowContact: this.state.allowContact
      }
    };
    this.props.registerUser(data, this.props.history);
  };
  componentDidUpdate() {
    if(Object.keys(this.state.errors).length !== 0){

      this.setState({
        errors: ""
      });
      this.notify('tr')
    }
  }
  notify = place => {
    var options = {};
    options = {
      place: place,
      message: (
        <div>
          <div>
           Error: {this.state.errors}
          </div>
        </div>
      ),
      type: 'warning',
      icon: "tim-icons icon-bell-55",
      autoDismiss: 7
    };
    this.refs.notificationAlert.notificationAlert(options);
  };
  render() {
    return (
      <>
        <Row>
        <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Col md='5' className='p-0'>
            <Card className='mb-0'>
              <CardImg width="100%" className='background-tint h-100' src={require("assets/img/theme/auth.png")}>
              </CardImg>
            </Card>
          </Col>
          <Col md='7' className='p-0 '>
            <Card className='h-100 mb-0'>
