import { FETCH_BOOKINGS_SUCCESS } from "../actions/types";

const initialState = {
    loading: false
};

export default function(state = initialState, action) {
  switch (action.type) {
      case FETCH_BOOKINGS_SUCCESS:
        loading = true;
        return action.payload;
        case FETCH_BOOKINGS_REQUEST:
            return {
                loading: true
            };
    default:
      return state;
  }
}
