import React from "react";
import {
  Button,
  Modal,
  ModalBody,
  ListGroup, ListGroupItem,
  Input,
  FormGroup,
  Form,
  Row,
  Col,
  Container

} from "reactstrap";
import { getCategories, createExpertService, getExpertServices, deleteExpertService } from "actions/serviceActions";
import { connect } from "react-redux";
class Services extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modalCategory: false,
      dropdownOpen: false,
      setDropdownOpen: false,
      categories: [],
      services: [],
      service: {
        serviceName: '',
        price: '',
        duration: '1h'
      },
      category: {}
    };
  }
  toggle = (prevState) => {
    this.setState({
      setDropdownOpen: !prevState
    });
  }
  componentDidMount() {
    this.props.getCategories();
    this.props.getExpertServices();
  }
  componentWillReceiveProps(nextProps) {
    this.setState({
      categories: nextProps.service.categories,
      services: nextProps.service.services
    })
  };
  toggleModalSearch = () => {
    this.setState({
      modalCategory: !this.state.modalCategory
    });
  };
  createExpertService = (event) => {
    event.preventDefault();
    const serviceData = {
      service: this.state.service,
      category: this.state.category
    }
    this.props.createExpertService(serviceData);
    this.props.getExpertServices();
    this.setState({
      modalCategory: !this.state.modalCategory,
      service: [],
      category: {}
    })
  };
  deleteExpertService = (service) => {
    this.deleteExpertService(service);
  };
  onChangeCategory = (event) => {
    let items = [...this.state.categories];
    let item = items.find(x => x.categoryName === event.target.value);
    this.setState({
      category: item
    })
  };
  toggleModal = (state, service) => {
    console.log('-----state----', state)
    this.setState({
      [state]: !this.state[state]
    });
  };
