import React from "react";
import { connect } from "react-redux";
import { getBookings, updateBooking } from "actions/bookingActions";
// reactstrap components
import { Card, CardBody, Row, Col, Table, ButtonGroup, Button, Modal, ModalBody } from "reactstrap";

class Bookings extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      current_view: "pending"
    };
  }

  componentDidMount() {
    const bookingData = {
      expert_id: "5f89b74c785a191b10dab1ac",
      status: 'pending'
    };

    this.props.getBookings(bookingData);

  }
  toggleModal = state => {
    this.setState({
      [state]: !this.state[state]
    });
  };
  acceptBooking = (booking) => {
    booking.status = 'upcoming';
    const bookingData = {
      expert_id: "5f89b74c785a191b10dab1ac",
      booking: booking
    }
    this.props.updateBooking(bookingData);
  }
  endBooking = (booking) => {
    booking.status = 'past';
    const bookingData = {
      expert_id: "5f89b74c785a191b10dab1ac",
      booking: booking
    }
    this.props.updateBooking(bookingData)
  }
  RejectBooking = (booking) => {
    // booking.status = 'past';
    // const bookingData = {
    //   expert_id: "5f89b74c785a191b10dab1ac",
    //   booking: booking
    // }
    // this.props.updateBooking(bookingData)
  }

  render() {
    const loading = !this.props.bookings.loading;
    return (
      <>
        <div className="content">
          {(this.state.current_view === 'pending') && <div><h1>Pending Bookings</h1></div>}
          {(this.state.current_view  === 'upcoming') && <div><h1>Upcoming Bookings</h1></div>}
          {(this.state.current_view === 'past') && <div><h1>Past Bookings</h1></div>}

          <Row>
            <Col>
              <Card>
                <CardBody>
                  <Table responsive>
                    <thead>
                      <tr>
                        <th className="text-center">Booking ID</th>
                        <th className="text-center">Customer</th>
                        <th className="text-center">Time</th>
                        <th className="text-center">Services</th>
                        <th className="text-center">Customer Address</th>
                        <th className="text-center">Instructions</th>
                        <th className="text-center">Payment</th>
                        <th className="text-center">  {(this.state.fetch_data !== 'past') && <div>Action</div>}</th>
                      </tr>
                    </thead>
                    <tbody>
                      {loading && (this.props.bookings.payload.map(booking => (
                        <tr key={booking._id}>
                          <td className="text-center"><p>{booking._id}<br></br><b>{booking.location}</b></p></td>
                          <td className="text-center "><p>{booking.customerName}</p></td>
                          <td className="text-center "><p>{booking.date}<br />{booking.time}</p></td>
                          <td className="text-center">
                            {booking.services.map(service => (
                              <p> {service} {``}</p>
                            ))
                            }
                          </td>
                          <td className="text-center w-5"><p>{booking.customerAddress}</p></td>
                          <td className="text-center w-5"><p>{booking.instructions}</p></td>
                          <td className="text-center"><p>{booking.payment}<br />â‚¬{booking.billingAmount}</p></td>
                          <td className="text-center">
                            {(booking.status === 'pending') &&
                              <ButtonGroup className="btn-group-vertical">
                                <Button style={{ marginBottom: "10px" }} color="success" onClick={() => {
                                  this.acceptBooking(booking);
                                }} size="md">Accept
                                  </Button> {` `}
                                <Button color="warning" size="md" onClick={() => this.toggleModal("notificationModal")}>Reject 
                                  </Button>
                              </ButtonGroup>}
                            {((booking.status  === 'upcoming' && booking.status !== 'past')) &&
                              <Button style={{ marginBottom: "10px" }} color="default" size="md">Complete
                                  </Button>
                            }
                          </td>
                        </tr>
                      )
                      ))}
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>
          </Row>
          <Modal
            modalClassName="modal-services"
            isOpen={this.state.modalCategory}
            toggle={this.toggleModalSearch}
          >
            <div className="modal-header justify-content-center">
              <button
                aria-label="Close"
                className="close"
                data-dismiss="modal"
                type="button"
                onClick={this.toggleModalSearch}
              >
                <i className="tim-icons icon-simple-remove" />
              </button>

            </div>
            <Form onSubmit={this.createExpertService}>
              <ModalBody>
                <h4 className="title title-up">Basic Information</h4>
                <div className="content">
                  <hr />
                  <Row>

                    <Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>Service Name</label>
                        <Input
                          required
                          onChange={this.onChange}
                          id="serviceName"
                          type="text"
                          value={this.state.service.serviceName}
                        />
                      </FormGroup>
                    </Col><Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>Category</label>
                        <Input
                          required
                          data-trigger=""
                          id="category"
                          type="select"
                          onChange={this.onChangeCategory}
                        >
                          {(this.state.categories) ? (
                            this.state.categories.map(category => {
                              return <option key={category._id}>{category.categoryName}</option>
                            })
                          ) : (
                              <option placeholder="true">Loading Categories</option>
                            )
                          }

                        </Input>
                      </FormGroup>
                    </Col>
                  </Row>
                </div>
              </ModalBody>
              <ModalBody>
                <div className="content" style={{
                  marginTop: "50px"
                }}>
                  <h4 className="title title-up">PRICING AND DURATION</h4>
                  <hr />
                  <Row>
                    <Col className="pr-md-1" md="4">
                      <FormGroup>
                        <label>Duration</label>
                        <Input
                          required
                          data-trigger=""
                          id="duration"
                          value={this.state.service.duration || '1h'}
                          type="select"
                          onChange={this.onChange}
                        >
                          <option>1h</option>
                          <option >1h 30m</option>
                          <option>2h</option>
                          <option >2h 30m</option>
                          <option>3h</option>
                        </Input>
                      </FormGroup>
                    </Col>
                    <Col className="px-md-1" md="4">
                      <FormGroup>
                        <label>Price</label>
                        <Input
                          required
                          onChange={this.onChange}
                          id="price"
                          value={this.state.service.price}
                          type="number"
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                </div>
                <Row style={{ marginTop: "30px" }}>
                  <Col>
                    <Button className="btn-block"
                      color="success"
                      type="submit"
                    >
                      Save
                  </Button>
                  </Col>
                </Row>
              </ModalBody>

            </Form>
          </Modal>
        </div>
      </>
    );
  }
}

function mapStateToProp(state) {
  return {
    bookings: state.bookings,
    view: state.view
  }
}
export default connect(
  mapStateToProp,
  { getBookings, updateBooking }
)(Bookings);
