import axios from "axios"
import { GET_ERRORS, FETCH_CATEGORIES_SUCCESS, CREATE_EXPERT_SERVICE} from "./types";

axios.defaults.headers.common = {
    'Authorization': 'JWT ' + localStorage.jwtToken
  };
export const getCategories = () => dispatch => {
    axios
      .get("/categories")
      .then(res => {
        dispatch({
            type: FETCH_CATEGORIES_SUCCESS,
            payload: res.data
        });
      })
      .catch(err =>
        dispatch({
          type: GET_ERRORS,
          payload: err.response.data
        })
      );
  };


  export const createExpertService = servicedata => dispatch => {
    axios
      .post("/expert/service/create", servicedata )
      .then(res => {
        dispatch({
            type: CREATE_EXPERT_SERVICE,
            payload: res.data
        });
      })
      .catch(err =>
        dispatch({
          type: GET_ERRORS,
          payload: err.response.data
        })
      );
  };
