import { FETCH_BOOKINGS_SUCCESS, FETCH_BOOKINGS_REQUEST } from "../actions/types";

const initialState = {
    loading: true,
    view: 'pending'
};

export default function(state = initialState, action) {
  switch (action.type) {
      case FETCH_BOOKINGS_SUCCESS:
        return {
            loading: false,
            payload: action.payload
        }
        case SET_BOOKING_PENDING_VIEW:
        return {
            view: 'pending'
        }
        case SET_BOOKING_UPCOMING_VIEW:
        return {
            view: 'upcoming'
        }
        case SET_BOOKING_PAST_VIEW:
        return {
            view: 'past'
        }
        case FETCH_BOOKINGS_REQUEST:
            return {
                ...state,
                loading: true
              };
    default:
      return state;
  }
}
