import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// react plugin used to create charts
import { Line, Bar, Doughnut } from "react-chartjs-2";

// reactstrap components
import {
    Button,
    ButtonGroup,
    Modal,
    ModalBody,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    UncontrolledDropdown,
    Label,
    FormGroup,
    Input,
    Table,
    Row,
    Col,
    UncontrolledTooltip, Container
} from "reactstrap";

// core components
import {
    chartExample1,
    chartAppointment,
    chartExample3,
    chartExample4
} from "variables/charts.js";

class Settings extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            modalBank: false,
            bigChartData: "data1"
        };
    }
    toggleModalBank = () => {
        this.setState({
            modalBank: !this.state.modalBank
        });
    };
    render() {
        return (
            <>
                <div className="content">
                    <Row>
                        <Col lg="3" />
                        <Col lg="3">
                            <Card style={{
                                textAlign: "center"
                            }} >
                                <CardHeader >
                                    <CardTitle tag="h3">
                                        Personal Details
                  </CardTitle>
                                </CardHeader>
                                <CardBody>
                                    <img
                                        alt="..."
                                        src={require("assets/img/icon_wallet.png")}
                                    />
                                </CardBody>
                            </Card>
                        </Col>
                        <Col lg="3">
                        <a style={{ cursor: 'pointer' }} onClick={this.toggleModalBank}>
                            <Card style={{
                                textAlign: "center"
                            }} >
                                <CardHeader>
                                    <CardTitle tag="h3">
                                        Bank Details
                  </CardTitle>
                                </CardHeader>
                                <CardBody>
                                    <img
                                        alt="..."

                                        src={require("assets/img/icon_salon_chair.png")}
                                    />
                                </CardBody>
                            </Card>
                            </a>
                        </Col>

                    </Row>
                </div>
            </>
        );
    }
}

export default Settings;
