import React from "react";
import { connect } from "react-redux";
import ReactDatetime from "react-datetime";
import moment from "moment";
import { getCurrentExpert,getBankInfo, updateCurrentExpert, getExpertScheduale, updateExpertScheduale, uploadImage, getExpertGallery } from "actions/userActions";
import SwiperCore, { Navigation, Pagination, Scrollbar, A11y, EffectCoverflow } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper.scss';
import {
  Button,
  Modal,
  ModalBody,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  FormGroup,
  Input,
  Row,
  Col,
  Container,
  Table
} from "reactstrap";
import NotificationAlert from "react-notification-alert";
import bg from "assets/img/flower-back.png";
import 'swiper/components/effect-coverflow/effect-coverflow.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
SwiperCore.use([EffectCoverflow]);
SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

class Settings extends React.Component {
  notify = place => {
    var options = {};
    options = {
      place: place,
      message: (
        <div>
          <div>
            Profile successfuly updated.
          </div>
        </div>
      ),
      type: 'success',
      icon: "tim-icons icon-bell-55",
      autoDismiss: 7
    };
    this.refs.notificationAlert.notificationAlert(options);
  };

  constructor(props) {
    super(props);
    this.state = {
      modalBank: false,
      modalPersonalDetails: false,
      modalPhotoGallery: false,
      modalAvailability: false,
      modalNotification: false,
      about: '',
      status: false,
      rowKey: null,
      isEdit: false,
      image: null,
      scheduale: {
        availability: []
      },
      slidesPerView:'auto',
      gallery: [],
      userId: '',
      ibanNumber: '',
      accountNumber: '',
      bankName: '',
      bankAddress: '',
      swiftCode: '',
      fullName: '',
      bankInfo: {
        fullName:''
      },
      userProfile: {
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        lang: '',
        phoneNumber: '',
        status: '',
        role: '',
        gender: '',
        dateOfBirth: '',
        venueName: '',
        residential: {
          street: '',
          number: '',
          city: '',
          country: ''
        },
        venue: {
          street: '',
          number: '',
          city: '',
          country: ''
        }
      }
    }
  }
  onEdit = (id) => {

    this.setState({
      status: true,
      rowKey: id
    })
  }

  onCancel = () => {
    const user = {
      expert_id: "5f89b74c785a191b10dab1ac",
      role: 'expert'
    };
    this.props.getExpertScheduale(user);
    this.setState({
      status: false,
      rowKey: null
    })
  }
  onSave = () => {
    const user = {
      scheduale: this.state.scheduale
    };
    this.props.updateExpertScheduale(user);
    this.setState({
      status: false,
      rowKey: null
    })
  }
  componentDidMount() {
    const user = {
      role: 'expert'
    };
    this.props.getBankInfo();
    this.props.getCurrentExpert(user);
    this.props.getExpertScheduale(user);
    this.props.getExpertGallery(user)
  }
  toggleModalBank = () => {
    this.setState({
      modalBank: !this.state.modalBank
    });
  };
  toggleModalGallery = () => {
    this.setState({
      modalPhotoGallery: !this.state.modalPhotoGallery
    });
  };
  togglePersonalDetails = () => {
    this.setState({
      modalPersonalDetails: !this.state.modalPersonalDetails
    });
  };
  toggleAvailability = () => {
    this.setState({
      modalAvailability: !this.state.modalAvailability
    });
  };
  handleProfileUpdate = () => {
    const current = {
      currentUser: this.state.userProfile
    };
    this.props.updateCurrentExpert(current);
    this.togglePersonalDetails();
    this.notify("br");
  }
  imageUpload = () => {
    let formData = new FormData();
    formData.append("photo", this.state.image);
    formData.append("user_id", '5f89b74c785a191b10dab1ac')
    this.props.uploadImage(formData);
    // if (event.target.files && event.target.files[0]) {
    //   this.props.uploadImage(imageFormObj);
    //   // this.setState({ image: event.currentTarget.files[0] })
    //   // let imageFormObj = new FormData();
    //   // imageFormObj.append("imageName", "5f89b74c785a191b10dab1ac" + Date.now());
    //   // imageFormObj.append("imageData", event.target.files[0]);
    //   // imageFormObj.append('user_id', "5f89b74c785a191b10dab1ac");

    //   // console.log('--imageFormObj--', imageFormObj);
    //   // this.props.uploadImage(imageFormObj);
    // }
    console.log('----------', this.state.image)
  }
  onChangeImage = (event) => {
    if (event.target.files && event.target.files[0]) {
      this.setState({ image: event.currentTarget.files[0] })
      // let imageFormObj = new FormData();
      // imageFormObj.append("imageName", "5f89b74c785a191b10dab1ac" + Date.now());
      // imageFormObj.append("imageData", event.target.files[0]);
      // imageFormObj.append('user_id', "5f89b74c785a191b10dab1ac");

      // console.log('--imageFormObj--', imageFormObj);
      // this.props.uploadImage(imageFormObj);
    }
  }
  handleFromChange = (event) => {

    let items = [...this.state.scheduale.availability];
    let item = items.find(x => x._id === this.state.rowKey);
    item.from = event.target.value;
    this.setState({
      data: items.map(el => (el._id === item._id ? { ...el, item } : el))
    }
    );
  }
  handleToChange = (event) => {

    let items = [...this.state.scheduale.availability];
    let item = items.find(x => x._id === this.state.rowKey);
    item.to = event.target.value;
    this.setState({
      data: items.map(el => (el._id === item._id ? { ...el, item } : el))
    }
    );
  }
  handleStatusChange = (event) => {

    let items = [...this.state.scheduale.availability];
    let item = items.find(x => x._id === this.state.rowKey);
    item.status = event.target.value;
    this.setState({
      data: items.map(el => (el._id === item._id ? { ...el, item } : el))
    }
    );
  }
  onChange = e => {
    let obj = this.state.userProfile;
    obj[e.target.id] = e.target.value;
    this.setState({ userProfile: obj })
  };
  onChangeDateOfBirth = (e) => {
    let date = moment(e.toDate()).format('MM/DD/YYYY');
    let obj = this.state.userProfile;
    obj.dateOfBirth = date;
    this.setState({ userProfile: obj })
  }
  onChangeResidential = e => {
    let obj = this.state.userProfile;
    obj.residential[e.target.id] = e.target.value;
    this.setState({ userProfile: obj })
  };
  onChangeVenue = e => {
    let obj = this.state.userProfile;
    obj.venue[e.target.id] = e.target.value;
    this.setState({ userProfile: obj })
  };
  onChangeBankInfo = e => {
    this.setState({ 
        [e.target.id]: e.target.value })
  };
  componentWillReceiveProps(nextProps) {
    this.setState({
      userProfile: nextProps.user.payload,
      scheduale: nextProps.user.scheduale,
      gallery: nextProps.user.gallery,
      bankInfo: nextProps.user.bankInfo
        })
  };

  render() {
    const loading = !this.props.user.loading;
    return (
      <>
        <div className="content"
          style={{
            backgroundImage: `url(${bg})`,
            backgroundPosition: "right top",
            backgroundRepeat: "no-repeat",
          }}
        >
          <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Container>
            <Row>        
              <Col lg="3"/>
              <Col lg="3">
                <Card className="card-settings" style={{
                  textAlign: "center",
                }} onClick={this.togglePersonalDetails}>
                  <CardHeader >
                    <CardTitle>
                      Personal Details
                                    </CardTitle>
                  </CardHeader>
                  <CardBody >
                    <img
                      alt="..."
                      src={require("assets/img/icon_salon_chair.png")}
                    />
                  </CardBody>
                </Card>
              </Col>
              <Col lg="3">
                <Card className="card-settings" style={{
                  textAlign: "center"
                }} onClick={this.toggleModalBank}>
                  <CardHeader>
                    <CardTitle >
                      Bank Details
                                    </CardTitle>
                  </CardHeader>
                  <CardBody >
                    <img
                      alt="..."
                      src={require("assets/img/icon_wallet.png")}
                    />
                  </CardBody>
                </Card>
              </Col>
            </Row>
            <Row>
              <Col lg="3" />
              <Col lg="3">
                <Card className="card-settings" style={{
                  textAlign: "center"
                }} onClick={this.toggleAvailability}>
                  <CardHeader >
                    <CardTitle>
                      Availability
                                    </CardTitle>
                  </CardHeader>
                  <CardBody >
                    <img
                      alt="..."
                      src={require("assets/img/icon_availability.png")}
                    />
                  </CardBody>
                </Card>
              </Col>
              <Col lg="3">
                <Card className="card-settings" style={{
                  textAlign: "center"
                }} onClick={this.toggleModalGallery}>
                  <CardHeader>
                    <CardTitle>
                      Photo Gallery
                                    </CardTitle>
                  </CardHeader>
                  <CardBody>
                    <img
                      alt="..."
                      src={require("assets/img/icon_photo.png")}
                    />
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </Container>
          <Modal
            modalClassName="modal-services"
            isOpen={this.state.modalBank}
            toggle={this.toggleModalBank}>
            <div className="modal-header justify-content-center">
              <button
                aria-label="Close"
                className="close"
                data-dismiss="modal"
                type="button"
                onClick={this.toggleModalBank}
              >
                <i className="tim-icons icon-simple-remove" />
              </button>
              <h4 className="title title-up">Bank Details</h4>
            </div>
            <ModalBody >
              { (loading) ? (
              <div className="content">
                <Row>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Expert ID</label>
                      <Input
                        disabled
                        value={this.state.userId}
                        type="number"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-md-1" md="6">
                    <FormGroup>
                      <label>Full Name</label>
                      <Input
                      onChange={this.onChangeBankInfo}
                        value={this.state.fullName}
                        type="text"
                        id="fullName"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>IBAN number</label>
                      <Input
                      onChange={this.onChangeBankInfo}
                      onChange={this.onChangeBankInfo}
                        value={this.state.ibanNumber}
                        type="text"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-md-1" md="6">
                    <FormGroup>
                      <label>Account number</label>
                      <Input
                      onChange={this.onChangeBankInfo}
                        value={this.state.accountNumber}
                        type="text"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Bank Name</label>
                      <Input
                      onChange={this.onChangeBankInfo}
                        value={this.state.bankName}
                        type="text"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-md-1" md="6">
                    <FormGroup>

                      <label>Bank Address</label>
                      <Input
                      onChange={this.onChangeBankInfo}
                        value={this.state.bankAddress}
                        type="text"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Swift Code</label>
                      <Input
                      onChange={this.onChangeBankInfo}
                        value={this.state.swiftCode}
                        type="text"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col>
                    <Button className="btn-block"
                      color="success"
                      type="button"
                    >
                      Save
                  </Button>
                  </Col>
                </Row>
              </div>) : (
                <div></div>
              )}
            </ModalBody>
          </Modal>
          <Modal
            modalClassName="modal-services"
            isOpen={this.state.modalPersonalDetails}
            toggle={this.togglePersonalDetails}>
            <div className="modal-header justify-content-center">
              <button
                aria-label="Close"
                className="close"
                data-dismiss="modal"
                type="button"
                onClick={this.togglePersonalDetails}
              >
                <i className="tim-icons icon-simple-remove" />
              </button>

            </div>
            <ModalBody >
              { (loading && this.state.userProfile) ? (
              <div className="content">
                <Row>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>First Name</label>
                      <Input
                        id="firstName"
                        value={this.state.userProfile.firstName || ''}
                        type="text"
                        onChange={this.onChange}
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-md-1" md="6">
                    <FormGroup>
                      <label>Second Name</label>
                      <Input
                        id="lastName"
                        value={this.state.userProfile.lastName || ''}
                        onChange={this.onChange}
                        type="text"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Phone</label>
                      <Input
                        id="phoneNumber"
                        value={this.state.userProfile.phoneNumber || ''}
                        onChange={this.onChange}
                        type="text"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Email</label>
                      <Input
                        id="email"
                        disabled
                        value={this.state.userProfile.email || ''}
                        onChange={this.onChange}
                        type="email"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>Gender</label>
                      <Input
                        data-trigger=""
                        id="gender"
                        name="gender"
                        type="select"
                        value={this.state.userProfile.gender || 'Male'}
                        onChange={this.onChange}
                      >
                        <option placeholder="true">Male</option>
                        <option defaultValue="2">Female</option>
                        <option defaultValue="3">Others</option>
                      </Input>
                    </FormGroup>

                  </Col>
                  <Col className="pr-md-1">
                    <FormGroup>
                      <label>Date of Birth</label>
                      <ReactDatetime
                        inputProps={{
                          placeholder: "Select Date"
                        }}
                        timeFormat={false}
                        value={'' || this.state.userProfile.dateOfBirth}
                        id="dateOfBirth"
                        onChange={this.onChangeDateOfBirth}

                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1">
                    <label>Residential Address</label>
                  </Col>
                </Row>
                <Row>
                  <Col className="pr-md-1" md="8">
                    <FormGroup>
                      <label>Street</label>
                      <Input
                        id="street"
                        onChange={this.onChangeResidential}
                        value={ '' || this.state.userProfile.residential.street}
                        type="text"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pr-md-1" md="2">
                    <FormGroup>
                      <label>No</label>
                      <Input
                        type="text"
                        value={'' || this.state.userProfile.residential.number}
                        onChange={this.onChangeResidential}
                        id="number"
                      />
                    </FormGroup>
                  </Col>

                </Row>
                <Row style={{ marginTop: "30px" }}>
                  <Col className="pr-md-1" md="6">
                    <FormGroup>
                      <label>City</label>
                      <Input
